enum KdfparamsType {
    Pbkdf2 {
            c: u32,
            dklen: u8,
            prf: String,
            salt: Vec<u8>,
        },
    Scrypt {
            dklen: u8,
            n: u32,
            p: u32,
            r: u32,
            salt: Vec<u8>,
        },
}

fn new<P, R, S>(P, &mut R, S, Option<&str>)
-> Result<(Vec<u8>, String), KeystoreError> where P: AsRef<Path>, R: Rng +
CryptoRng, S: AsRef<[u8]>

fn encrypt_key<P, R, B, S>(P, &mut R, B, S, Option<&str>)
-> Result<String, KeystoreError> where P: AsRef<Path>, R: Rng + CryptoRng,
B: AsRef<[u8]>, S: AsRef<[u8]>
